mapper.select:
SELECT 

FROM employee a

WHERE a.id = #{id}

SELECT 

FROM employee a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.companyid = #{companyid}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.sex = #{sex}


		AND a.identityno = #{identityno}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM employee a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE employee SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
